import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Transaction } from '../models/transaction.model';

@Injectable({
  providedIn: 'root'
})
export class WalletService {
  private apiUrl = 'YOUR_WALLET_API_URL';
  private transactions: Transaction[] = [];

  constructor(private http: HttpClient) { 
    this.transactions = [
      new Transaction(0, 0, 500, 'add', new Date('2023-09-01T10:00:00')),
      new Transaction(1, 0, 200, 'deduct', new Date('2023-09-02T14:30:00')),
      new Transaction(2, 1, 1000, 'add', new Date('2023-09-01T11:45:00')),
      new Transaction(3, 2, 300, 'add', new Date('2023-09-02T09:15:00')),
      new Transaction(4, 2, 400, 'deduct', new Date('2023-09-02T17:20:00')),    
      new Transaction(5, 3, 800, 'add', new Date('2023-09-01T15:10:00')),
      new Transaction(6, 3, 200, 'deduct', new Date('2023-09-02T12:05:00')),
    ]
  }

  adjustBalance(userId: number, amount: number): Observable<number> {
    const requestBody = { userId, amount };
    return this.http.post<number>(`${this.apiUrl}/adjust-balance`, requestBody);
  }
  getTransactionHistory(userId: string): Transaction[] {
    return this.transactions;
  }

  // getTransactionHistory(userId: string): Observable<any[]> {
  //   return this.http.get<any[]>(`${this.apiUrl}/transaction-history/${userId}`);
  // }
}
