import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { WalletService } from '../services/wallet.service';

@Component({
  selector: 'app-adjust-balance',
  templateUrl: './adjust-balance.component.html',
  styleUrls: ['./adjust-balance.component.css']
})
export class AdjustBalanceComponent implements OnInit {
  users: any[] = [];
  selectedUser: string = '';
  adjustmentAmount: number = 0;

  constructor(
    private userService: UserService,
    private walletService: WalletService
  ) { }

  ngOnInit(): void {
    this.userService.getUsers().subscribe((users: any[]) => {
      this.users = users;
    });
  }

  adjustBalance(): void {
    this.walletService.adjustBalance(this.selectedUser, this.adjustmentAmount)
      .subscribe((updatedBalance: any) => {
        // Handle updated balance
      });
  }
}
