import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { WalletService } from '../services/wallet.service';
import { User } from '../models/user.model';

@Component({
  selector: 'app-adjust-balance',
  templateUrl: './adjust-balance.component.html',
  styleUrls: ['./adjust-balance.component.css']
})
export class AdjustBalanceComponent implements OnInit {
  users: User[] = [];
  // selectedUser: string = '';
  selectedUser: number = 2;
  adjustmentAmount: number = 0;
  adjustmentType: 'withdrawal' | 'deposit' = 'deposit';
  currBalance: number = 0;

  constructor(
    private userService: UserService,
    private walletService: WalletService
  ) { }

  ngOnInit(): void {
    this.users = this.userService.getUsers();
  }

  getBalance(): void {
    this.users.find(user => user.id === this.selectedUser)?.balance;
    // this.adjustmentAmount = this.users.find(this.selectedUser).balance;
  }
  adjustBalance(): void {
    this.userService.setUsers(this.users.map(user => {
      if (user.id == this.selectedUser) {
        console.log({ ...user, balance: this.adjustmentAmount })
        return { ...user, balance: this.adjustmentAmount };
      }
      return user;

  }));
  console.log(this.userService.getUsers())
  //   this.walletService.adjustBalance(this.selectedUser, this.adjustmentType ,this.adjustmentAmount)
  //     .subscribe((updatedBalance: any) => {
  //       // Handle updated balance
  // });

  }}
