import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Transaction } from '../models/transaction.model';
import { BalanceAdjustmentResponse } from '../interfaces/BalanceAdjustmentResponse';
import { catchError } from 'rxjs/operators';
import { throwError } from 'rxjs';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class WalletService {
  private apiUrl = 'http://localhost:3000/users';
  private transactions: Transaction[] = [];

  constructor(private http: HttpClient) { 
    const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
    this.transactions = [
      new Transaction(0, 0, 500, 'deposit', '2023-09-01'),
      new Transaction(1, 0, 200, 'withdrawal', '2023-09-02'),
      new Transaction(2, 1, 1000, 'deposit', '2023-09-01'),
      new Transaction(3, 2, 300, 'deposit', '2023-09-02'),
      new Transaction(4, 2, 400, 'withdrawal', '2023-09-02'),    
      new Transaction(5, 3, 800, 'deposit', '2023-09-01'),
      new Transaction(6, 3, 200, 'withdrawal', '2023-09-02'),
    ]
  }

  // adjustBalance(userId: string, amount: number, adjustType: 'withdrawal' | 'deposit'): Observable<any> {
  //   const requestBody = { "amount":amount, "type":adjustType};
  //   console.log(requestBody)
  //   console.log(`${this.apiUrl}/${userId}/adjust-balance`)
  //   return this.http.post<any>(`${this.apiUrl}/${userId}/adjust-balance`, requestBody);
  // }
  adjustBalance(userId: string, amount: number, adjustType: 'withdrawal' | 'deposit'): Observable<any> {
    const requestBody = { "amount": amount, "type": adjustType };
    console.log( this.http.post<any>(`${this.apiUrl}/${userId}/adjust-balance`, requestBody))
    return this.http.post<any>(`${this.apiUrl}/${userId}/adjust-balance`, requestBody)
      .pipe(
        map((response: HttpResponse<any>) => {
          const statusCode = response.status;
          return statusCode;
        })
        // catchError((error) => {
        //   console.error('Error:', error);
        //   return throwError(error);
        // })
      )
  }

  getAllTransactions():Transaction[] {
    return this.transactions;
  }
  // getTransactionHistory(userId: number, startDate: string , endDate:string): Transaction[] {
  //   // return this.transactions.filter((transaction) => transaction.userId == userId );

  //   return this.transactions.filter((transaction) => transaction.userId == userId && transaction.timestamp >= startDate && transaction.timestamp <= endDate);
  //   ;
  // }

  getTransactionHistory(userId: string, startDate:string , endDate:string): Observable<Transaction[]> {
    return this.http.get<Transaction[]>(`${this.apiUrl}/${userId}/transactions`);
  }
}
