import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Transaction } from '../models/transaction.model';

@Injectable({
  providedIn: 'root'
})
export class WalletService {
  private apiUrl = 'YOUR_WALLET_API_URL';
  private transactions: Transaction[] = [];

  constructor(private http: HttpClient) { 
    const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
    this.transactions = [
      new Transaction(0, 0, 500, 'deposit', (new Date('2023-09-01')).toLocaleDateString('en-EG')),
      new Transaction(1, 0, 200, 'withdrawal', (new Date('2023-09-02')).toLocaleDateString('en-EG')),
      new Transaction(2, 1, 1000, 'deposit', (new Date('2023-09-01')).toLocaleDateString('en-EG')),
      new Transaction(3, 2, 300, 'deposit', (new Date('2023-09-02')).toLocaleDateString('en-EG')),
      new Transaction(4, 2, 400, 'withdrawal', (new Date('2023-09-02')).toLocaleDateString('en-EG')),    
      new Transaction(5, 3, 800, 'deposit', (new Date('2023-09-01')).toLocaleDateString('en-EG')),
      new Transaction(6, 3, 200, 'withdrawal', '2023-09-02'),
    ]
  }

  adjustBalance(userId: number, amount: number): Observable<number> {
    const requestBody = { userId, amount };
    return this.http.post<number>(`${this.apiUrl}/adjust-balance`, requestBody);
  }
  getAllTransactions():Transaction[] {
    return this.transactions;
  }
  getTransactionHistory(userId: number, startDate: string , endDate:string): Transaction[] {
    return this.transactions.filter((transaction) => transaction.userId == userId );

    // return this.transactions.filter((transaction) => transaction.userId == userId && transaction.timestamp >= startDate && transaction.timestamp <= endDate);
    ;
  }

  // getTransactionHistory(userId: string): Observable<any[]> {
  //   return this.http.get<any[]>(`${this.apiUrl}/transaction-history/${userId}`);
  // }
}
